import React, { useState, useEffect, useRef } from 'react';
import { CSSTransition } from 'react-transition-group';

function SideNavBar() {
        const [activeMenu, setActiveMenu] = useState('main');
        const [menuHeight, setMenuHeight] = useState(null);
        const dropdownRef = useRef(null);
        const [classType, setClassType] = useState("");

        const handleMiddleShift = (direction) => {
            direction == "back" ? setClassType("menu-primary") : setClassType("menuSecondary");
        } 

        useEffect(() => {
          const height = dropdownRef.current?.firstChild.offsetHeight;
          setMenuHeight(dropdownRef.current?.firstChild.offsetHeight)
        }, [])

        function calcHeight(el) {
          const height = el.offsetHeight;
          setMenuHeight(height);
        }


        function DropdownItem(props) {
          return (
            <a href={props.href} className="menu-item" onClick={() => {
                if(props.goToMenu == "main"){
                    setClassType("menu-secondary",
                        setActiveMenu(props.goToMenu)
                    );
                } else if(props.toThirdMenu){
                    console.log('here')
                    setClassType("menu-primary",
                        setActiveMenu(props.goToMenu)
                    );
                } else{
                    props.goToMenu && setActiveMenu(props.goToMenu);
                    }
                }
            }>
              <span className="icon-button">{props.leftIcon}</span>
              {props.children}
              <span className="icon-right">{props.rightIcon}</span>
            </a>
          );
        }

        return (
          <div className="dropdown" style={{ height: menuHeight }} ref={dropdownRef}>

            <CSSTransition
              in={activeMenu === 'main'}
              timeout={500}
              classNames="menu-primary"
              unmountOnExit
              onEnter={calcHeight}>
              <div className="menu">
              <DropdownItem href="/">
              Главная
                </DropdownItem>
                <DropdownItem goToMenu="type">Продукты</DropdownItem>
                <DropdownItem href="/blogs/browse?category=all&page=1">
                Блог
                </DropdownItem>
                <DropdownItem href="/stores">
                Где купить
                </DropdownItem>
                <DropdownItem href="/about_us">
                О нас
                </DropdownItem>

              </div>
            </CSSTransition>



            <CSSTransition
              in={activeMenu === 'type'}
              timeout={500}
              classNames={classType}
              unmountOnExit
              onEnter={calcHeight}>
              <div className="menu">
                <DropdownItem goToMenu="main">
                <i className="fas fa-long-arrow-alt-left" style={{marginRight: "10px"}} onClick={()=>handleMiddleShift("back")} ></i>Back
                </DropdownItem>
                <DropdownItem href="/products/all">Все продукты</DropdownItem>
                <DropdownItem toThirdMenu={true} goToMenu="product">Тип продукта</DropdownItem>
                <DropdownItem toThirdMenu={true} goToMenu="skin">Тип Кожи</DropdownItem>
                <DropdownItem toThirdMenu={true} goToMenu="collection" >Коллекции</DropdownItem>
                <DropdownItem toThirdMenu={true} goToMenu="care" >Тип Ухода</DropdownItem>
              </div>
            </CSSTransition>



            <CSSTransition
              in={activeMenu === 'product'}
              timeout={500}
              classNames="menu-secondary"
              unmountOnExit
              onEnter={calcHeight}>
              <div className="menu">
                <DropdownItem goToMenu="type" >
                <i className="fas fa-long-arrow-alt-left mr-1" style={{marginRight: "10px"}} ></i>Back
                </DropdownItem>               
                <DropdownItem href="/products/all?type=Bundle" >Набор</DropdownItem>
                <DropdownItem href="/products/all?type=Cleanser" >Очищение</DropdownItem>
                <DropdownItem href="/products/all?type=Cream" >Крем</DropdownItem>
                <DropdownItem href="/products/all?type=Eye Cream" >Крем для глаз</DropdownItem>
                <DropdownItem href="/products/all?type=Lotion" >Лосьон</DropdownItem>
                <DropdownItem href="/products/all?type=Serum">Сыворотка</DropdownItem>
                <DropdownItem href="/products/all?type=Toner" >Тоник</DropdownItem>
              </div>
            </CSSTransition>

            <CSSTransition
              in={activeMenu === 'skin'}
              timeout={500}
              classNames="menu-secondary"
              unmountOnExit
              onEnter={calcHeight}>
              <div className="menu">
                <DropdownItem goToMenu="type" >
                <i className="fas fa-long-arrow-alt-left" style={{marginRight: "10px"}} ></i> Назад
                </DropdownItem>
                <DropdownItem href="/products/all?skin=Dry" >Сухая</DropdownItem>
                <DropdownItem href="/products/all?skin=Normal">Нормальная</DropdownItem>
                <DropdownItem href="/products/all?skin=Oily">Жирная</DropdownItem>
              </div>
            </CSSTransition>

            <CSSTransition
              in={activeMenu === 'collection'}
              timeout={500}
              classNames="menu-secondary"
              unmountOnExit
              onEnter={calcHeight}>
              <div className="menu">
                <DropdownItem goToMenu="type" >
                <i className="fas fa-long-arrow-alt-left" style={{marginRight: "10px"}} ></i>Назад
                </DropdownItem>
                <DropdownItem href="/products/all?collection=Drops of Benefits">Drops of Benefits</DropdownItem>
                <DropdownItem href="/products/all?collection=Game On">Game On</DropdownItem>
                <DropdownItem href="/products/all?collection=Glow Up">Glow Up</DropdownItem>
              </div>
            </CSSTransition>


            <CSSTransition
              in={activeMenu === 'care'}
              timeout={500}
              classNames="menu-secondary"
              unmountOnExit
              onEnter={calcHeight}>
              <div className="menu">
                <DropdownItem goToMenu="type" >
                <i className="fas fa-long-arrow-alt-left" style={{marginRight: "10px"}} ></i>Назад
                </DropdownItem>
                <DropdownItem href="/products/all?care=Basic">Базовый</DropdownItem>
                <DropdownItem href="/products/all?care=Advanced">Продвинутый</DropdownItem>
              </div>
            </CSSTransition>
          </div>
        );
}

export default SideNavBar;
