export default function reducer(
  state = {
    token: null,
    userId: null,
    role: "user",
    cart: [],
    loginWidget: true,
    report: {
      id: null,
      category: null
    }
  },
  action
) {
  switch (action.type) {
    case "login":
      return {
        ...state,
        token: action.payload.token,
        userId: action.payload.userId,
        role: action.payload.role,
      };
    case "logout":
      return { ...state, token: null, userId: null, role: "user" };
    case "loginWidgetOn":
      return {...state, loginWidget: false};
      case "loginWidgetOff":
        return {...state, loginWidget: true};
    case "report":
      return {...state, report: {id: action.payload.id, category: action.payload.category }}
    case "deleteReport":
      return {...state, report: {id: null, category: null}}


    case "loadState":
      return action.payload;
    default:
      return state
  }
}
