import React, { useState } from "react";
import { Navbar, Container, Row, Col } from "react-bootstrap";
import SideNavBar from "./sideNavBar";

function HalfScreenNav() {
  const [colSize, setColSize] = useState({
    content: "",
    wrap: "",
  });
  const [sideNavClass, setSideNavClass] = useState("");

  function openNav() {
    setSideNavClass("sidenavOpen");
    document.getElementById("main").style.marginLeft = "100%";
    document.getElementById("mySidenav").style.width = "103%";
    if (
      /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(
        navigator.userAgent
      )
    ) {
      document.getElementsByClassName("sidenavContent")[0].style.width = "100%";
      setColSize({
        content: "12",
        wrap: "0",
      });
    } else {
      document.getElementsByClassName("sidenavContent")[0].style.width =
        "300px";
      setColSize({
        content: "",
        wrap: "",
      });
    }
  }

  /* Set the width of the side navigation to 0 and the left margin of the page content to 0, and the background color of body to white */
  function closeNav() {
    setSideNavClass("sidenavClose");
    setTimeout(() => {
    document.getElementById("mySidenav").style.width = "0";
    document.getElementById("main").style.marginLeft = "0";
    }, 200)
  }

  return (
    <div style={{ width: "100%" }}>
      <Navbar
        className="p-3"
        style={{ padding: "0" }}
        bg=""
        expand="lg"
      >
        <Container>
          <Navbar.Toggle onClick={openNav} aria-controls="basic-navbar-nav" />
          <Navbar.Brand href="/" className="">
            {" "}

              <img
                src="https://numee-kazakhstan.s3.eu-central-1.amazonaws.com/demo/logo numee.png"
                style={{ height: "30px" }}
              />

          </Navbar.Brand>

        </Container>
      </Navbar>
      <Row id="mySidenav" className="sidenav">
      <Col className={sideNavClass} sm={colSize.wrap}></Col>
        <Col className="sidenavContent" sm={colSize.content}>
          <div
            className="navHeader text-center"
            style={{ borderBottom: "1px solid black", position: "relative" }}
          >
            <a
              className="closebtn"
              onClick={closeNav}
            >
              &times;
            </a>
          </div>

        </Col>
      </Row>
      <div id="main" hidden></div>
    </div>
  );
}

export default HalfScreenNav;
