import React, { useState, useEffect, useRef } from "react";
import { Button, Form } from "react-bootstrap";

import { CSSTransition } from "react-transition-group";

function NavMenu() {
  const [activeMenu, setActiveMenu] = useState("main");
  const [menuHeight, setMenuHeight] = useState(null);
  const dropdownRef = useRef(null);
  const [classType, setClassType] = useState("");

  const handleMiddleShift = (direction) => {
    direction == "back"
      ? setClassType("menu-primary")
      : setClassType("menuSecondary");
  };

  useEffect(() => {
    const height = dropdownRef.current?.firstChild.offsetHeight;
    setMenuHeight(dropdownRef.current?.firstChild.offsetHeight);
  }, []);

  function calcHeight(el) {
    const height = el.offsetHeight;
    setMenuHeight(height);
  }

  function DropdownItem(props) {
    return (
      <a
        href={props.href}
        className="menu-item"
        onClick={() => {
          if (props.goToMenu == "main") {
            setClassType("menu-secondary", setActiveMenu(props.goToMenu));
          } else if (props.toThirdMenu) {
            console.log("here");
            setClassType("menu-primary", setActiveMenu(props.goToMenu));
          } else {
            props.goToMenu && setActiveMenu(props.goToMenu);
          }
        }}
      >
        <span className="icon-button">{props.leftIcon}</span>
        {props.children}
        <span className="icon-right">{props.rightIcon}</span>
      </a>
    );
  }

  return (
    <div className="dropdown" style={{ height: menuHeight }} ref={dropdownRef}>
      <CSSTransition
        in={activeMenu === "main"}
        timeout={500}
        classNames="menu-primary"
        unmountOnExit
        onEnter={calcHeight}
      >
        <div className="menu">
          <DropdownItem goToMenu="addText">
            <Button className="menuButton">Add Text</Button>
          </DropdownItem>

          <DropdownItem goToMenu="addImage">
            <Button className="menuButton">Add Image</Button>
          </DropdownItem>

          <DropdownItem goToMenu="addAudio">
            <Button className="menuButton">Add Audio</Button>
          </DropdownItem>
        </div>
      </CSSTransition>

      <CSSTransition
        in={activeMenu === "addText"}
        timeout={500}
        classNames="menu-secondary"
        unmountOnExit
        onEnter={calcHeight}
      >
        <div className="menu">
          <Form.Control placeholder="Type you message..." as="textarea" rows={12} />
          <Form.Control placeholder="Name" as="text" />
          <Form.Control as="text" />
        </div>
      </CSSTransition>

      <CSSTransition
        in={activeMenu === "skin"}
        timeout={500}
        classNames="menu-secondary"
        unmountOnExit
        onEnter={calcHeight}
      >
        <div className="menu">
          <DropdownItem goToMenu="type">
            <i
              className="fas fa-long-arrow-alt-left"
              style={{ marginRight: "10px" }}
            ></i>{" "}
            Назад
          </DropdownItem>
          <DropdownItem href="/products/all?skin=Dry">Сухая</DropdownItem>
          <DropdownItem href="/products/all?skin=Normal">
            Нормальная
          </DropdownItem>
          <DropdownItem href="/products/all?skin=Oily">Жирная</DropdownItem>
        </div>
      </CSSTransition>

      <CSSTransition
        in={activeMenu === "collection"}
        timeout={500}
        classNames="menu-secondary"
        unmountOnExit
        onEnter={calcHeight}
      >
        <div className="menu">
          <DropdownItem goToMenu="type">
            <i
              className="fas fa-long-arrow-alt-left"
              style={{ marginRight: "10px" }}
            ></i>
            Назад
          </DropdownItem>
          <DropdownItem href="/products/all?collection=Drops of Benefits">
            Drops of Benefits
          </DropdownItem>
          <DropdownItem href="/products/all?collection=Game On">
            Game On
          </DropdownItem>
          <DropdownItem href="/products/all?collection=Glow Up">
            Glow Up
          </DropdownItem>
        </div>
      </CSSTransition>

      <CSSTransition
        in={activeMenu === "care"}
        timeout={500}
        classNames="menu-secondary"
        unmountOnExit
        onEnter={calcHeight}
      >
        <div className="menu">
          <DropdownItem goToMenu="type">
            <i
              className="fas fa-long-arrow-alt-left"
              style={{ marginRight: "10px" }}
            ></i>
            Назад
          </DropdownItem>
          <DropdownItem href="/products/all?care=Basic">Базовый</DropdownItem>
          <DropdownItem href="/products/all?care=Advanced">
            Продвинутый
          </DropdownItem>
        </div>
      </CSSTransition>
    </div>
  );
}

export default NavMenu;
