import React, { useState } from "react";
import { Navbar, Container, Row, Col, NavItem, Nav } from "react-bootstrap";
import SideNavBar from "./sideNavBar";

function Header() {
  const [colSize, setColSize] = useState({
    content: "",
    wrap: "",
  });
  const [sideNavClass, setSideNavClass] = useState("");

  function openNav() {
    setSideNavClass("sidenavOpen");
    document.getElementById("main").style.marginLeft = "100%";
    document.getElementById("mySidenav").style.width = "103%";
    if (
      /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(
        navigator.userAgent
      )
    ) {
      document.getElementsByClassName("sidenavContent")[0].style.width = "100%";
      setColSize({
        content: "12",
        wrap: "0",
      });
    } else {
      document.getElementsByClassName("sidenavContent")[0].style.width =
        "300px";
      setColSize({
        content: "",
        wrap: "",
      });
    }
  }

  /* Set the width of the side navigation to 0 and the left margin of the page content to 0, and the background color of body to white */
  function closeNav() {
    setSideNavClass("sidenavClose");
    setTimeout(() => {
      document.getElementById("mySidenav").style.width = "0";
      document.getElementById("main").style.marginLeft = "0";
    }, 200);
  }

